import { downloadWebsite } from "./downloadWeb.js";
import fs from 'fs/promises';

const token = "eyJhbGciOiJIUzUxMiJ9..mZI4XvU8zf9xsOJZJRMv2m7cSJrlcZjH436UyNys153DSjU2yi1JHzpM65VuaSID6j2BziguQGJdyAYryCdLgA";

export const getLocalVersion = async ({ urls, outputPath, sitemap }) => {
    await fs.mkdir(outputPath, { recursive: true });
    try {
        for (let i = 0; i < urls.length; i++) {
            const url = urls[i];
            await downloadWebsite({
                startUrl: url,
                outputPath,
                options: {
                    maxDepth: 2,
                    sameDomainOnly: true,
                },
                sitemap,
                token,
                index: i
            });
        }

    } catch (error) {
        console.error('Error during website download:', error);
        throw error;
    }
};

